package basic.datatype;

public class IntegerExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*
        # 리터럴: 변수에 저장되기 전의 상수값.
        자바의 정수 리터럴의 타입은 int 입니다.
        int보다 더 큰 범위의 수를 표현하기 위해서는 뒤에 L을 붙여서
        long타입으로 인식시켜야 합니다.
        */

		byte a = 127; //byte가 표현할 수 있는 최대의 수
		short b = 32767; //short가 표현할 수 있는 최대의 수
		int c = 2147483647; //int가 표현할 수 있는 최대의 수
		long d = 2147483648L; //long이 표현할 수 있는 최대의 수 L을 추가해야 한다.
		// L = long이라는 뜻 소문자도 가능
		

	}

}
